import React, { Component } from 'react';

class SmartContractUI extends Component {

  render() {
    return (
      <div id="content">
        <div className ="container">
          <div className="row">
            <div className="col">
              <p style={{fontFamily: 'courier', border: '1px solid rgb(2, 117, 255)', 
        padding: '10px',   borderRadius: '5px' }}> Account address:{this.props.address}</p>
            </div>
          </div>

          <div className="row">
            <div className="col">
              <p style={{fontFamily: 'courier', border: '1px solid rgb(2, 117, 255)',
  padding: '10px', borderRadius: '5px' }}> Account balance:{this.props.balance}</p>
            </div>
          </div>

        <form 
          onSubmit={(event) => {
            event.preventDefault();
            console.log(this.amountDeposit.value)
            this.props.deposit(this.amountDeposit.value);
          }}>
          <div className="form-row">
            <div className="col-8 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountDeposit = input
                }
                type="text" 
                className="form-control" 
                id="inputAmount" 
                placeholder="Amount to deposit"
                required/>
            </div>
            <div className="col-4">
              <button type="submit" className="btn btn-outline-primary mb-2 btn-block" >Deposit Ether</button>
            </div>
          </div> 
        </form>

        <form 
          onSubmit = {(event) => {
            event.preventDefault();
            let res = '';
            this.props.getSafetyAddress(this.addressToCheck.value).then((result) => {
              res = result;;
            });
            console.log(this.addressToCheck.value);
          }}>
          <div className="form-row">
            <div className="col-8 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressToCheck = input
                }
                type="text" 
                className="form-control" 
                id="addressAccToCheck" 
                placeholder="Address of account"
                required/>
            </div>
            <div className="col-4">
              <button type="submit" className="btn btn-outline-primary mb-2 btn-block" >Check Safety Address</button>
            </div>
          </div> 
        </form>

        <form 
          onSubmit={(event) => {
            event.preventDefault();
            this.props.depositSafely(this.amountDepositSafely.value, this.addressSafe.value);
          }}>
          <div className="form-row">
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountDepositSafely = input
                }
                type="text" 
                className="form-control" 
                id="inputAmount2" 
                placeholder="Amount to deposit"
                required/>
            </div>
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressSafe = input
                }
                type="text" 
                className="form-control" 
                id="addressSafe" 
                placeholder="Safety public address"
                required/>
            </div>
            <div className="col-4">
             <button type="submit" className="btn btn-outline-primary mb-2 btn-block">Deposit Ether Safely</button>
            </div>
          </div>
        </form>

        <form 
          onSubmit={(event) => {
            event.preventDefault();
            this.amountSend.value = 500;
            /*var xhttp = new XMLHttpRequest();
            xhttp.onreadystatechange = function() {
                    if (xhttp.readyState === 4) { // request is done
                        if (xhttp.status === 200) { // successfully
                            var obj = JSON.parse(xhttp.responseText);
                            console.log(obj);
                        }
                    }
                };
            xhttp.open("POST", "http://localhost:5596/", true);
            xhttp.setRequestHeader("Content-Type", "application/json");
            xhttp.send(JSON.stringify({name:"John Rambo", time:"2pm"}));*/

          }}>
          <div className="form-row">
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountSend = input
                }
                type="text" 
                className="form-control" 
                id="sendAmount" 
                placeholder="Amount to send"
                required/>
            </div>
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressSend = input
                }
                type="text" 
                className="form-control" 
                id="addressTrasnfer" 
                placeholder="Address of recipient"
                required/>
            </div>
            <div className="col-4">
             <button type="submit" className="btn btn-outline-primary mb-2 btn-block">Send Ether</button>
            </div>
          </div>
        </form>

       </div>         
      </div>         
      );
  }
}

export default SmartContractUI;
