import React, { Component } from 'react';

class todoList extends Component {

  render() {
    return (
      <div id="content">
        <p style={{fontFamily: 'courier', border: '1px solid powderblue',
  padding: '10px', }}> Account address:{this.props.address}</p>
        <p style={{fontFamily: 'courier', border: '1px solid powderblue',
  padding: '10px', }}> Account balance:{this.props.checkBalance(this.props.address)}</p>
        <form class="form-inline">
          <div class="form-group mx-sm-3 mb-2">
            <label for="inputAmount" class="sr-only">Amount</label>
            <input type="text" class="form-control" id="inputAmount" placeholder="Amount to deposit"/>
          </div>
          <button type="submit" class="btn btn-outline-primary mb-2">Deposit Ether</button>
        </form>
        <form onSubmit={(event) => {
          event.preventDefault();
//this.props.createTask(this.task.value);
        }}>
          <input 
            id="newTask" 
            ref={(input) => 
              this.task = input
            } 
            type="text" 
            className="form-control" 
            placeholder="Add task..." 
            required/>
          <input type="submit" hidden={true}/>
        </form>

      </div>         
      );
  }
}

export default todoList;
