import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Modal.css';

const ModalTrigger = ({toggle}) => <button className="smartButtonLong" onClick={toggle}>enable two-factor authentication</button>;
const ModalContent = ({toggle, onKeyDown, children}) => {
	return ReactDOM.createPortal(
		<aside className="c-modal-cover" onKeyDown={onKeyDown} tabIndex="0">
		  <div className="c-modal">
		    <button className="c-modal__close" onClick={toggle}>
		      <svg className="c-modal__close-icon" viewBox="0 0 40 40"><path d="M 10,10 L 30,30 M 30,10 L 10,30"></path></svg>
		    </button>
		    <div className="c-modal__body">
		      {children}
		    </div>
		 </div>
		</aside>,
		document.body
	);
};

class Modal extends React.Component {

  constructor(props) {
  	super(props);
  	this.state = {
  		isOpen: false
  	}
  }

  toggle = () => {
	this.setState({ isOpen: !this.state.isOpen });
  };

  onKeyDown = (event) => {
  	console.log(event.keyCode);
	return event.keyCode === 27 && this.toggle();
  }

  render() {
  	return (
      <div>
       <ModalTrigger toggle={this.toggle}/>
       {
       	this.state.isOpen && 
       	<ModalContent toggle={this.toggle} onKeyDown={this.onKeyDown}> Text
       	</ModalContent>
       }
      </div>
    );
  }
}

export default Modal;
