import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Modal.css';

const ModalTrigger = ({handleSubmit, handleInputChange, errors}) => 
        <form id="DepositForm" onSubmit={handleSubmit} noValidate>
          <input type="text" name="amountToDeposit" onChange={handleInputChange} className="smartInput2" placeholder="Amount"/>
          <input type="text" name="privateKey" onChange={handleInputChange} className="smartInput2" placeholder="Private Key"/>
          <button type="submit" className="smartButton">deposit</button>
          {errors.amountToDeposit.length > 0 && <span className='errorMultiple'>{errors.amountToDeposit}</span>}
          {errors.privateKey.length > 0 && <span className='errorMultiple'>{errors.privateKey}</span>}
        </form>;
const ModalContent = ({toggle, modalRef, onKeyDown, onClickAway, children}) => {
	return ReactDOM.createPortal(
		<aside className="c-modal-cover" onKeyDown={onKeyDown} onClick={onClickAway} tabIndex="0">
		  <div className="c-modal-safety" ref={modalRef}>
		    <button className="c-modal__close" onClick={toggle}>
		      <svg className="c-modal__close-icon" viewBox="0 0 40 40"><path d="M 10,10 L 30,30 M 30,10 L 10,30"></path></svg>
		    </button>
		    <div className="c-modal__body">
		      {children}
		    </div>
		 </div>
		</aside>,
		document.body
	);
};

class ModalDeposit extends React.Component {

  constructor(props) {
  	super(props);
  	this.state = {
      amountToDeposit: '',
      privateKey: '',
  		isOpen: false,
      errors: {
        amountToDeposit: '',
        privateKey: ''
      },
      typed: false
      }
  }

  handleInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;

    let errors = this.state.errors;

    switch (name) {
    case 'amountToDeposit': 
      if (isNaN(value)) {
        errors.amountToDeposit = 'Amount must be a number';
      } else {
        errors.amountToDeposit = '';
      }
      break;
    case 'privateKey':
      if (value.length != 64) {
        errors.privateKey = 'Private key length must be 64';
      } else if (value.length == 0) {
        errors.privateKey = '';
      } else {
        errors.privateKey = '';
      }
    default:
      break;
  }

    this.setState({errors, [name]: value, typed:true});
  }

  handleSubmit = (event) => {
    event.preventDefault();
    if(this.state.errors.amountToDeposit.length == 0 && this.state.errors.privateKey.length == 0 && this.state.typed == true) {
      console.error('Valid Form');
    }else{
      console.error('Invalid Form');
    }
  }


  toggle = () => {
	this.setState({ 
		isOpen: !this.state.isOpen, 
	  });
  };

  onKeyDown = (event) => {
	return event.keyCode === 27 && this.toggle();
  }

  onClickAway = (event) => {
	  if (this.modalNode && this.modalNode.contains(event.target)) return;
	  this.toggle();
	};


  render() {

  	return (
      <span>
       <ModalTrigger 
        handleSubmit={this.handleSubmit} 
        handleInputChange={this.handleInputChange}
        errors={this.state.errors}/>
       {
       	this.state.isOpen && 
       	<ModalContent 
       		toggle={this.toggle} 
       		onKeyDown={this.onKeyDown}
       		onClickAway={this.onClickAway}
       		modalRef={n => this.modalNode = n}> 
       		<p className="modalHeader">Your current safety address is</p> 
       	</ModalContent>
       }
      </span>
    );
  }
}

export default ModalDeposit;
