import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import '../Modal.css';
var Tx = require('ethereumjs-tx').Transaction;

const ModalTrigger = ({handleSubmit, handleInputChange, errors}) => 
        <form id="DepositForm" onSubmit={handleSubmit}>
          <input type="text" name="amountToDeposit" onChange={handleInputChange} className="smartInput3" placeholder="Amount"
            required pattern="\d+"/>
          <input type="text" name="safetyPubKey" onChange={handleInputChange} className="smartInput3" placeholder="Safety Public"
            required minLength="42" maxLength="42" pattern="0x\w+"/>
          <input type="text" name="privateKey" onChange={handleInputChange} className="smartInput3" placeholder="Private Key"
            required minLength="64" maxLength="64" pattern="\w+"/>
          <button type="submit" className="smartButton">deposit erc safely</button>
        </form>;
const ModalContent = ({closeModal, modalRef, onKeyDown, onClickAway, children}) => {
	return ReactDOM.createPortal(
		<aside className="c-modal-cover" onKeyDown={onKeyDown} onClick={onClickAway} tabIndex="0">
		  <div className="c-modal-safety" ref={modalRef}>
		    <button className="c-modal__close" onClick={closeModal}>
		      <svg className="c-modal__close-icon" viewBox="0 0 40 40"><path d="M 10,10 L 30,30 M 30,10 L 10,30"></path></svg>
		    </button>
		    <div className="c-modal__body">
		      {children}
		    </div>
		 </div>
		</aside>,
		document.body
	);
};

class ModalDepositERCSafely extends React.Component {

  constructor(props) {
  	super(props);
  	this.state = {
      txHash: '',
      txReceipt: '',
      safetyPubKeY: '',
      privateKey: '',
  		isOpen: false,
      hashReceipt: false,
      confirmationReceipt:false
      }
  }

  handleInputChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
    case 'amountToDeposit': 
      if (event.target.validity.patternMismatch) {
        event.target.setCustomValidity("Please input a number");
      } else {
        event.target.setCustomValidity("");
      }  
      break;
    case 'privateKey':
      if (event.target.validity.tooShort) {
        event.target.setCustomValidity("Private key has to be 64 characters");
      } else if (event.target.validity.patternMismatch) {
        event.target.setCustomValidity("Only alphanumeric characters are allowed");
      } else {
        event.target.setCustomValidity("");
      }
      break;
    case 'safetyPubKeY':
      if (event.target.validity.tooShort) {
        event.target.setCustomValidity("Public key has to be 42 characters");
      } else if (event.target.validity.patternMismatch) {
        event.target.setCustomValidity("Public key has to start with '0x'");
      } else {
        event.target.setCustomValidity("");
      }     
    default:
      break;
    }

    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.deposit(this.state.amountToDeposit, this.state.safetyPubKeY, this.state.privateKey);
  }

  openModal = () => {
	this.setState({ 
		isOpen: true, 
	  });
  };

  closeModal = () => {
  this.setState({ 
    isOpen: false, 
    });
  };

  onKeyDown = (event) => {
	return event.keyCode === 27 && this.closeModal();
  }

  onClickAway = (event) => {
	  if (this.modalNode && this.modalNode.contains(event.target)) return;
	  this.closeModal();
	};

  deposit = (amountToDeposit, safetyPubKey, privateKey) => {

    let web3 = this.props.web3;
    let multisigERC20Token = this.props.multisigERC20Token;
    let multisigERC20Address = this.props.multisigERC20Address;
    let modal = this;
    let tokenSymbol = this.props.tokenSymbol;
    let addrFrom = this.props.address;
    const priv = Buffer.from(privateKey, 'hex');

    // Getting Ethereum transaction count
    web3.eth.getTransactionCount(addrFrom, (err, txCount) => {
      // Retrieving the current nonce inside the contract
      multisigERC20Token.methods.transactionNonces(addrFrom).call({from: addrFrom}, (error, nonce) => {
            if (error) {
                console.log(error)
            } else {

                // Build the transaction
                web3.eth.getGasPrice().then((gasPrice) => {
                    console.log('Current gas price: ', gasPrice);    
                    multisigERC20Token.methods.depositFunds(safetyPubKey, tokenSymbol, amountToDeposit).estimateGas({gas: gasPrice, from: addrFrom}, function(error, gasAmount) {
                        if (error) {
                            console.log(error);

                        } else {
                        console.log('Estimate of gas usage: ', gasAmount);

                        const txObject = {
                            nonce: web3.utils.toHex(txCount),
                            gasLimit: web3.utils.toHex(gasAmount), 
                            gasPrice: web3.utils.toHex(gasPrice),
                            to: multisigERC20Address,
                            data: multisigERC20Token.methods.depositFunds(safetyPubKey, tokenSymbol, amountToDeposit).encodeABI()
                        };
                        console.log(txObject);

                        // Sign the transaction
                        const tx = new Tx(txObject, { chain: 'ropsten', hardfork: 'petersburg' });
                        tx.sign(priv);

                        const serializedTransaction = tx.serialize();
                        const rawTx = '0x' + serializedTransaction.toString('hex');

                        console.log(rawTx);

                        // Broadcast the transaction
                        web3.eth.sendSignedTransaction(rawTx)
                        .once('transactionHash', function(hash){ 
                            console.log('Hash of transaction: ', hash);
                            modal.setState({ 
                              txHash: hash,
                              hashReceipt: true,
                              confirmationReceipt: false                       
                            });
                            modal.openModal();
                        })
                        .once('confirmation', function(confNumber, receipt){ 
                            console.log('Transaction confirmation number: ', confNumber);
                            console.log('Second receipt of transaction: ', receipt);

                            modal.setState({ 
                              txReceipt: receipt,
                              confirmationReceipt: true, 
                              hashReceipt: false
                            });
                            modal.openModal(); 
                        })
                        .on('error', function(error){ console.log(error) });

                        }
                    })
                })
            }
    })
    })
  } 

  render() {

  	return (
      <span>
       <ModalTrigger 
        handleSubmit={this.handleSubmit} 
        handleInputChange={this.handleInputChange}
        errors={this.state.errors}/>
       {
       	this.state.isOpen && 
       	<ModalContent 
       		closeModal={this.closeModal} 
       		onKeyDown={this.onKeyDown}
       		onClickAway={this.onClickAway}
       		modalRef={n => this.modalNode = n}> 
       		{this.state.hashReceipt ? 
            <p className="modalTextTx"><span style={{color: 'black'}}>Transaction hash is:</span><br/>
            {this.state.txHash}
            </p> 
            : null} 
          {this.state.confirmationReceipt ?
            <React.Fragment>
            <p className="modalTextTx">Transaction confirmed!</p> 
            <hr/>
            <p className="modalTextTx">
            <span className="modalTextBlack">Please view the receipt:</span><br/>
            <span className="modalTextBlack">Transaction hash is:</span><br/>
            {this.state.txReceipt.transactionHash}<br/>
            <span className="modalTextBlack">Block hash is:</span><br/>
            {this.state.txReceipt.blockHash}<br/>
            <span className="modalTextBlack">Block number is:</span><br/>
            {this.state.txReceipt.blockNumber}<br/>
            <span className="modalTextBlack">Gas used:</span><br/>
            {(parseInt(this.state.txReceipt.gasUsed)).toString()}
            </p> 
            </React.Fragment>
            : null} 

       	</ModalContent>
       }
      </span>
    );
  }
}

export default ModalDepositERCSafely;
