import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Modal.css';

const ModalTrigger = ({toggle}) => <button className="smartButtonLong" onClick={toggle}>enable two-factor authentication</button>;
const ModalContent = ({toggle, modalRef, onKeyDown, onClickAway, children}) => {
	return ReactDOM.createPortal(
		<aside className="c-modal-cover" onKeyDown={onKeyDown} onClick={onClickAway} tabIndex="0">
		  <div className="c-modal" ref={modalRef}>
		    <button className="c-modal__close" onClick={toggle}>

		    </button>
		    <div className="c-modal__body">
		      {children}
		    </div>
		 </div>
		</aside>,
		document.body
	);
};

class Modal extends React.Component {

  constructor(props) {
  	super(props);
  	this.state = {
  		isOpen: false
  	}
  }

  toggle = () => {
	this.setState({ isOpen: !this.state.isOpen });
  };

  onKeyDown = (event) => {
	return event.keyCode === 27 && this.toggle();
  }

  onClickAway = (event) => {
	  if (this.modalNode && this.modalNode.contains(event.target)) return;
	  this.toggle();
	};
  render() {
  	return (
      <div>
       <ModalTrigger toggle={this.toggle}/>
       {
       	this.state.isOpen && 
       	<ModalContent 
       		toggle={this.toggle} 
       		onKeyDown={this.onKeyDown}
       		onClickAway={this.onClickAway}
       		modalRef={n => this.modalNode = n}> 
       		Text
       	</ModalContent>
       }
      </div>
    );
  }
}

export default Modal;
