import React, { Component } from 'react';

import Modal from './ModalV1';

class SmartContractUI extends Component {

  render() {
    let my = this;
    function handleClick(e) {
      e.preventDefault();
      my.props.getNonce().then(result => { alert(result); 
      });
    }

    const inputStyle = {
      height: "100%", 
      borderColor: "purple",
      borderRadius: "2px"
    }

    return (
      <div id="content">
        <div className ="container">
          <div className="row">
            <div className="col">
              <p style={{fontFamily: 'courier', border: '1px solid rgb(2, 117, 255)', 
        padding: '10px',   borderRadius: '5px' }}> Account address:{this.props.address}</p>
            </div>
          </div>

          <div className="row">
            <div className="col">
              <p style={{fontFamily: 'courier', border: '1px solid rgb(2, 117, 255)', 
        padding: '10px',   borderRadius: '5px' }}> Ether balance (wei):{this.props.etherBalance}</p>
            </div>
          </div>

          <div className="row">
            <div className="col">
              <p style={{fontFamily: 'courier', border: '1px solid rgb(2, 117, 255)',
  padding: '10px', borderRadius: '5px' }}> Contract balance (wei):{this.props.balance}</p>
            </div>
          </div>

        <form 
          className="form-row"
          onSubmit={(event) => {
            event.preventDefault();
            console.log(this.amountDeposit.value)
            this.props.deposit(this.amountDeposit.value);
          }}>
            <div className="col-8 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountDeposit = input
                }
                type="text" 
                className="form-control" 
                id="inputAmount" 
                placeholder="Amount to deposit"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4">
              <button type="submit" className="btn btn-outline-primary mb-2 btn-block" >Deposit Ether</button>
            </div>
        </form>

        <form 
          onSubmit = {(event) => {
            event.preventDefault();
            this.props.getSafetyAddress(this.addressToCheck.value).then((result) => {
              alert(result);
            });
          }}>
          <div className="form-row">
            <div className="col-8 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressToCheck = input
                }
                type="text" 
                className="form-control" 
                id="addressAccToCheck" 
                placeholder="Address of account"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4">
              <button type="submit" className="btn btn-outline-primary mb-2 btn-block" >Safe Address</button>
            </div>
          </div> 
        </form>

        <form 
          onSubmit={(event) => {
            event.preventDefault();
            this.props.depositSafely(this.amountDepositSafely.value, this.addressSafe.value);
          }}>
          <div className="form-row">
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountDepositSafely = input
                }
                type="text" 
                className="form-control" 
                id="inputAmount2" 
                placeholder="Amount to deposit"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressSafe = input
                }
                type="text" 
                className="form-control" 
                id="addressSafe" 
                placeholder="Safety public address"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4">
             <button type="submit" className="btn btn-outline-primary mb-2 btn-block">Safe Deposit</button>
            </div>
          </div>
        </form>

        <form 
          onSubmit={(event) => {
            event.preventDefault();
            var addr = this.addressSend.value;
            var amount = this.amountSend.value;
            var my = this;

            this.props.getNonce().then((result) => {
              var xhttp = new XMLHttpRequest();
              var _nonce = result;
              console.log(_nonce);
              console.log(addr)
              console.log(amount);
              var data =
              { addressFrom: this.props.address,
                addressTo: addr, 
                amount: amount,
                nonce: _nonce
              };
            xhttp.onreadystatechange = function() {
                    if (xhttp.readyState === 4) { // request is done
                        if (xhttp.status === 200) { // successfully
                            var obj = JSON.parse(xhttp.responseText);
                            console.log({ 
                              addressFrom: my.props.address,
                              addressTo: addr, 
                              amount: amount, 
                              signature: obj.signature});
                              my.props.transfer(addr, amount, obj.signature);
                        }
                    }
                };
            xhttp.open("POST", "http://localhost:5596/", true);
            xhttp.setRequestHeader("Content-Type", "application/json");
            xhttp.send(JSON.stringify(data));
            })
          }}>
          <div className="form-row">
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.amountSend = input
                }
                type="text" 
                className="form-control" 
                id="sendAmount" 
                placeholder="Amount to send"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4 input-group mb-2">
              <input 
                ref={(input) => 
                  this.addressSend = input
                }
                type="text" 
                className="form-control" 
                id="addressTrasnfer" 
                placeholder="Address of recipient"
                style={inputStyle}
                required/>
            </div>
            <div className="col-4">
             <button type="submit" className="btn btn-outline-primary mb-2 btn-block">Send Ether</button>
            </div>
          </div>
        </form>

        <div>
             <button onClick={handleClick} className="btn btn-outline-primary mb-2 btn-block">Check Current Nonce</button>
        </div>

        <div>
        <Modal address={this.props.address}/>
        </div>
       </div>         
      </div>         
      );
  }
}

export default SmartContractUI;
