import React, { Component } from 'react';
import { Button, Col, Container, Media, Modal, ModalHeader, ModalBody, ModalFooter, Row } from 'reactstrap';

class ModalV1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      secret: ''
    };

    this.toggle = this.toggle.bind(this);
  	}

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  generateSecret = () => {
  	var xhttp = new XMLHttpRequest();
    var data = { address: this.props.address };
    xhttp.onreadystatechange = () => {
            if (xhttp.readyState === 4) { // request is done
                if (xhttp.status === 200) { // successfully
                    var obj = JSON.parse(xhttp.responseText);
                    console.log(obj.secret);
                    this.setState({ secret: obj.secret });
                }
            }
        };
    xhttp.open("POST", "http://localhost:5596/totp-generate", true);
    xhttp.setRequestHeader("Content-Type", "application/json");
    xhttp.send(JSON.stringify(data));
  }

  render() {
  	const externalCloseBtn = <button className="close" style={{ position: 'absolute', top: '15px', right: '15px' }} onClick={this.toggle}>&times;</button>;
    return (
      <div>
        <Button outline color="primary" className="btn mb-2" block onClick={() => { this.toggle(); this.generateSecret(); }}>enable two-factor authentication</Button>{' '}
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} external={externalCloseBtn}>
          <ModalHeader>2FA With Google Authenticator</ModalHeader>
          <ModalBody>
	        <Container>
		        <Row>
		          <Col xs="3"></Col>
		          <Col xs="6">
		          	<Media>
			          <Media object src={this.state.secret} alt="Generic placeholder image" />
			        </Media>
		          </Col>
		          <Col xs="3"></Col>
		        </Row>
	        </Container>		              
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default ModalV1;
