class ModalV1 extends Component {
	constructor(props) {
		super(props);
		this.state = {
			show : false
		};
	}

	handleClose = () => { this.setState({ show : false })};
	handleShow = () => { this.setState({ show : true })};

	render() {
		return (
			<div>
		      <button variant="primary" onClick={handleShow}>
