import React, { Component } from 'react';
import '../Smart.css';
import ModalTokenAllowance from './ModalTokenAllowance.js'; 


class ERC20 extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	       chosenTokenBalance: 'tokenImperialBalance',
           chosenToken: this.props.tokenImperial
	    }
  	};

    handleChange = (event) => {
        this.setState({
            chosenTokenBalance: event.target.value,
            chosenToken: this.props[event.target.id]
        });
    }

  	render() {

    return (
      <div>
        <p className="smartP"> Your address: {this.props.address}</p>
        <div className="smartDiv">
            <select name="tokens" className="smartSelect" onChange={this.handleChange}>
                <option id='tokenImperial' value="tokenImperialBalance">Token Imperial</option>
                <option id='tokenDemocratic' value="tokenDemocraticBalance">Token Democratic</option>
            </select>
        </div>
        <p className="smartP"> Your token balance: {this.props[this.state.chosenTokenBalance]}</p>
        <ModalTokenAllowance 
            multisigERC20Address={this.props.multisigERC20Address}
            token={this.props.chosenToken}
            web3={this.props.web3}
        />
      </div>         
      );
  }
}

export default ERC20;
