import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Modal.css';

const ModalTrigger = ({onOpen}) => <button className="smartButtonLong" onClick={onOpen}>enable two-factor authentication</button>;
const ModalContent = () => {
	return ReactDOM.createPortal(
		<aside className="c-modal-cover">
		  <div className="c-modal">
		    <button className="c-modal__close">
		      <span className="u-hide-visually">Close</span>
		      <svg className="c-modal__close-icon" viewBox="0 0 40 40"><path d="M 10,10 L 30,30 M 30,10 L 10,30"></path></svg>
		    </button>
		    <div className="c-modal__body">
		      CONTENT WILL GO HERE
		    </div>
		 </div>
		</aside>,
		document.body
	);
};

class Modal extends React.Component {

  constructor(props) {
  	super(props);
  	this.state = {
  		isOpen: false
  	}
  }

  onOpen = () => {
	this.setState({ isOpen: true });
  };

  render() {

  	return (
      <div>
       <ModalTrigger onOpen={this.onOpen}/>
       {this.state.isOpen && <ModalContent />}
      </div>
    );
  }
}

export default Modal;
