import React, { Component } from 'react';
import '../Smart.css';

class SelectBox extends Component {
    constructor(props) {
        super(props);
        this.state = {
           items: this.props.items || [],
           showItems: false,
           selectedItem: this.props.items && this.props.items[0]
        }
    };

    dropDown = () => {
        this.setState({
            showItems: !this.state.showItems
        })
    }

    selectItem = (item) => {
        this.setState({
            selectedItem: item,
            showItems: false
        })
    }

    render() {

    return (
      <React.Fragment>
              <div 
                className="selectBoxArrow"
                onClick={this.dropDown}>
                <span 
                    className={`${this.state.showItems? 'selectBoxArrowUp' : 'selectBoxArrowDown'}`} />
              </div>  
              <div style={{display: this.state.showItems? 'block' : 'none'}} className="selectBoxItems">
                {
                    this.state.items.map(item => <div 
                            key={ item.id }
                            onClick={() => this.selectItem(item)}
                            className={this.state.selectedItem === item ? 'selected' : ''}
                        >

                            { item.value }
                        </div>)
                }
             </div>   
      </React.Fragment>  
      );
    }
}
class ERC20 extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	       chosenTokenBalance: 'tokenImperialBalance'
	    }
  	};

    handleChange = (event) => {
        this.setState({chosenTokenBalance: event.target.value});
    }

  	render() {

    return (
      <div>
        <p className="smartP"> Your address: {this.props.address}</p>
        <div style={{ position: 'relative' }}>
            <SelectBox 
                items={[
                    { value: 'United States', id: 1 },
                    { value: 'United Kingdom', id: 2 },
                    { value: 'Ukraine', id: 3 }
                ]} 
            />
        </div>
        <div className="smartDiv">
            <select name="tokens" className="smartSelect" onChange={this.handleChange}>
                <option value="tokenImperialBalance">Token Imperial</option>
                <option value="tokenDemocraticBalance">Token Democratic</option>
            </select>
            <p className="smartErcP"> balance: {this.props[this.state.chosenTokenBalance]}</p>
        </div>
      </div>         
      );
  }
}

export default ERC20;
